import {useState ,useEffect} from 'react';
import './App.css';
import TaskBlock from './component/Task/TaskBlock';
import Tab from './component/Tab';


function App() {

  const [filteredTitle, setFilteredTitle] = useState('未完成');
  const [tabNum, setTabNum] = useState({todoNum: 0, doneNum: 0, allNum: 0})
  useEffect(()=>{
    const storedTasks = JSON.parse(localStorage.getItem('TASKS'));
    const todoNum = storedTasks.filter(item => !item.state).length
    const doneNum = storedTasks.filter(item => item.state).length
    const allNum = storedTasks.length
    setTabNum({todoNum, doneNum, allNum})
  },[])
  const captureTasksHandler = (obj)=>{
    setFilteredTitle(obj.title)
  }
  const tabNumHandler=(numObj)=>{
    setTabNum(numObj)
  }
  return (
    <div className="App">
      <Tab onTab={captureTasksHandler} todoNum={tabNum.todoNum} doneNum={tabNum.doneNum} allNum={tabNum.allNum}/>
      <TaskBlock title={filteredTitle} onTaskNum={tabNumHandler} />
    </div>
  );
}

export default App;
