import { useRef } from 'react';

const NewTask = (props) => {
  const taskRef = useRef();
  const radioRef = useRef();
  const inputValueHandler = (event) => {
    props.onTaskValue({
      value: taskRef.current.value,
      state: radioRef.checked,
    })
  };
  return (
    <div>
      <input type="radio" ref={radioRef}/>
      <input type="text" ref={taskRef} onBlur={inputValueHandler} />
    </div>
  );
};

export default NewTask;
