import React, { useState ,useContext } from 'react';
import TaskContext from '../../store/task-context';
import NewTask from './NewTask';
import classes from './TaskBlock.module.css';

const TaskBlock = (props) => {
  const taskCtx = useContext(TaskContext);
  const [checkboxState, setCheckboxState] = useState();
  const [itemId, setItemId] = useState();
  const [inputValue, setInputValue] = useState();

  let taskList = <p>沒有提醒事項</p>;

  const stateChangeHandler = (event)=>{
    setCheckboxState(event.target.value);
  };
  const valueChangerHandler = (event)=>{
    setInputValue(event.target.value)
  };
  const idGetHandler = (event)=>{
    setItemId(event.target.value)

  }
  const updateTaskHandler =()=>{
    taskCtx.addTask({
      id: itemId,
      value: inputValue,
      state: checkboxState
    })
  }

  if (taskCtx.tasks.length > 0) {
    taskList = (
      <ul>
        {taskCtx.tasks.map((task) => (
          <li key={task.id} onBlur={updateTaskHandler} onClick={idGetHandler}>
            <div className={classes['task']}>
              <input
                className={classes.checkbox}
                type="checkbox"
                onChange={stateChangeHandler}
              />
              <input className={classes.text} type="text" value={inputValue} onChange={valueChangerHandler}>{task.value}</input>
            </div>
          </li>
        ))}
      </ul>
    );
  }

  return (
    <div>
      <h2>未完成</h2>
      <NewTask />
      {taskList}
    </div>
  );
};

export default TaskBlock;
