import {useState } from 'react';
import './App.css';
import TaskBlock from './component/Task/TaskBlock';
import Tab from './component/Tab';


function App() {

  const [filteredTitle, setFilteredTitle] = useState('未完成');
  const [tabNum, setTabNum] = useState({})
  const captureTasksHandler = (obj)=>{
    setFilteredTitle(obj.title)
  }
  const tabNumHandler=(numObj)=>{
    setTabNum(numObj)
  }
  return (
    <div className="App">
      <Tab onTab={captureTasksHandler} tabNum={tabNum}/>
      <TaskBlock title={filteredTitle} onTaskNum={tabNumHandler} />
    </div>
  );
}

export default App;
