import {useState} from 'react';
import './App.css';
import TaskBlock from './component/Task/TaskBlock';
import Tab from './component/Tab';


function App() {

  const [filteredTasks, setFilteredTasks] = useState([]);
  const captureTasksHandler = (tasks)=>{
    setFilteredTasks(tasks)
  }

  return (
    <div className="App">
      <Tab onTab={captureTasksHandler} />
      <TaskBlock tabTasks={filteredTasks} />
    </div>
  );
}

export default App;
