import classes from './TaskBlock.module.css';
import { useState } from 'react';
import NewTask from './NewTask';
import TaskItem from './TaskItem';
// import TaskContext from '../../store/task-context';

const TaskBlock = (props) => {
  // const taskCtx = useContext(TaskContext);

  const [tasks, setTasks] = useState([]);
  const addTaskHandler = (taskObj) => {
    setTasks((prevTasks) => [taskObj, ...prevTasks]);
  };
  const checkboxChangeHandler = (updateObj) => {
    const clickItemIndex = tasks.findIndex((task) => task.id === updateObj.id);
    const clickItem = tasks[clickItemIndex];

    const updatedTask = { ...clickItem, state: updateObj.state };
    let updatedTasks = [...tasks];
    updatedTasks[clickItemIndex] = updatedTask;
    setTasks(updatedTasks);
  };

  let taskList = <p>沒有提醒事項</p>;
  let filteredNum = 0;
  if (props.title === '已完成') {
    const filteredTask = tasks.filter((task) => task.state === true);
    filteredNum = filteredTask.length;
    taskList = (
      <ul>
        {filteredTask.map((task) => (
          <TaskItem
            id={task.id}
            key={task.id}
            state={task.state}
            value={task.value}
            onChangeState={checkboxChangeHandler}
          />
        ))}
      </ul>
    );
  }
  if (props.title === '未完成') {
    const filteredTask = tasks.filter((task) => task.state === false);
    filteredNum = filteredTask.length;

    taskList = (
      <ul>
        {filteredTask.map((task) => (
          <TaskItem
            id={task.id}
            key={task.id}
            state={task.state}
            value={task.value}
            onChangeState={checkboxChangeHandler}
          />
        ))}
      </ul>
    );
  }

  if (props.title === '全部') {
    filteredNum = tasks.length;
    taskList = (
      <ul>
        {tasks.map((task) => (
          <TaskItem
            id={task.id}
            key={task.id}
            value={task.value}
            state={task.state}
            onChangeState={checkboxChangeHandler}
          />
        ))}
      </ul>
    );
  }
  return (
    <div className={classes['tab-block']}>
      <div className={classes['tab-title']}>
        <h2>{props.title}</h2>
        <h2>{filteredNum}</h2>
      </div>
      {taskList}
      <NewTask addTask={addTaskHandler} />
    </div>
  );
};

export default TaskBlock;
