import {useState, useContext} from 'react';
import './App.css';
import TaskBlock from './component/Task/TaskBlock';
import Tab from './component/Tab';
import TaskContext from './store/task-context';

function App() {
  const taskCtx = useContext(TaskContext);
  const [tasks,setTasks] = useState(taskCtx.tasks)
  const [filteredTitle, setFilteredTitle] = useState('未完成');
  const captureTasksHandler = (obj)=>{
    if(obj.title==='未完成'){
      const filteredTask = tasks.filter(task=> task.state===false)
      setTasks(filteredTask)
    }

    if(obj.title==='已完成'){
      const filteredTask = tasks.filter(task=> task.state===true)
      setTasks(filteredTask)
    }

    if(obj.title==='全部'){
      const filteredTask = taskCtx.tasks
      setTasks(filteredTask)
    }
    setFilteredTitle(obj.title)
  }

  return (
    <div className="App">
      <Tab onTab={captureTasksHandler} />
      <TaskBlock title={filteredTitle} tasks={tasks} />
    </div>
  );
}

export default App;
