import { useState, useContext } from 'react';
import classes from './TaskItem.module.css';
import TaskContext from '../../store/task-context';

const TaskItem = (props) => {
  const [checked, setChecked] = useState(props.state);

  const taskCtx = useContext(TaskContext);

  const stateChangeHandler = (event) => {
    setChecked((checked)=> !checked);
    props.onChangeState({
      id: props.id,
      value: props.value,
      state: checked,
    });
  };

  const styleClasses = `${classes.checkbox} ${checked? classes.checked : ''}`
  return (
    <li key={props.id} className={classes['task']} onClick={stateChangeHandler}>
      <span className={styleClasses} />
      <div className={classes.text}>{props.value}</div>
    </li>
  );
};

export default TaskItem;
