import classes from './TaskBlock.module.css';
import { useEffect, useState } from 'react';
import NewTask from './NewTask';
import TaskItem from './TaskItem';
// import TaskContext from '../../store/task-context';

const todoListFn = (items)=>{return items.filter((task) => task.state === false)}
const doneListFn = (items)=>{return items.filter((task) => task.state === true)}
const TaskBlock = (props) => {
  // const taskCtx = useContext(TaskContext);

  // const [tasks, setTasks] = useState([]);
  const {onAdd,onDelete,onToggle,onDeleteDone,tasks,isShown,onShowNew,onCloseNew} = props;
  // const [isShown, setIsShown] = useState(false);
  const filteredDoneTask = tasks.filter((task) => task.state === true);
  const filteredTodoTask = tasks.filter((task) => task.state === false);


  // useEffect(()=>{
  //   const storedTasks = JSON.parse(localStorage.getItem('TASKS'));
  //   setTasks(storedTasks)
  // },[])

  // useEffect(()=>{
  //   localStorage.setItem('TASKS',JSON.stringify(tasks))
  // },[tasks])

  // const addTaskHandler = (taskObj) => {
  //     setTasks((prevTasks) => [taskObj, ...prevTasks]);
  //     // const lastestTasks = [taskObj ,...tasks ]
  //     setIsShown(false);
  //     const tabNumObj = {
  //       todoNum:  tasks.filter((task) => task.state === false).length,
  //       doneNum:  tasks.filter((task) => task.state === true).length,
  //       allNum: tasks.length,
  //     };
  //     props.onTaskNum(tabNumObj);
  //     console.log(tabNumObj)
  // };
  // const checkboxChangeHandler = (updateObj) => {
  //   const clickItemIndex = tasks.findIndex((task) => task.id === updateObj.id);
  //   const clickItem = tasks[clickItemIndex];

  //   const updatedTask = { ...clickItem, state: updateObj.state };
  //   let updatedTasks = [...tasks];
  //   updatedTasks[clickItemIndex] = updatedTask;
  //   setTasks(updatedTasks);

  //   const tabNumObj = {
  //     todoNum: todoListFn(updatedTasks).length,
  //     doneNum: doneListFn(updatedTasks).length,
  //     allNum: updatedTasks.length,
  //   };
  //   props.onTaskNum(tabNumObj);
  // };


  // const deleteTaskHandler = (id) => {
  //   const updatedTasks = tasks.filter((task) => task.id !== id);
  //   setTasks(updatedTasks);
  // };
  // const deleteDoneTaskHandler = () => {
  //   const updatedTasks = tasks.filter((task) => task.state === false);
  //   setTasks(updatedTasks);

  //   const tabNumObj = {
  //     todoNum: todoListFn(updatedTasks).length,
  //     doneNum: doneListFn(updatedTasks).length,
  //     allNum: updatedTasks.length,
  //   };
  //   props.onTaskNum(tabNumObj);
  // };
  let taskList = <p>沒有提醒事項</p>;
  let filteredNum = 0;
  let tabTitleColor = classes.todo;

  if (props.title === '已完成') {
    filteredNum = filteredDoneTask.length;
    tabTitleColor = classes.done;
    taskList = (
      <ul>
        {filteredDoneTask.map((task) => (
          <TaskItem
            id={task.id}
            key={task.id}
            state={task.state}
            value={task.value}
            onDelete={onDelete}
            onChangeState={onToggle}
          />
        ))}
      </ul>
    );
  }
  if (props.title === '未完成') {
    filteredNum = filteredTodoTask.length;
    tabTitleColor = classes.todo;
    taskList = (
      <ul>
        {filteredTodoTask.map((task) => (
          <TaskItem
            id={task.id}
            key={task.id}
            state={task.state}
            value={task.value}
            onDelete={onDelete}
            onChangeState={onToggle}
          />
        ))}
      </ul>
    );
  }

  if (props.title === '全部') {
    filteredNum = tasks.length;
    tabTitleColor = classes.all;
    taskList = (
      <ul>
        {tasks.map((task) => (
          <TaskItem
            id={task.id}
            key={task.id}
            value={task.value}
            state={task.state}
            onDelete={onDelete}
            onChangeState={onToggle}
          />
        ))}
      </ul>
    );
  }
  return (
    <div className={classes['tab-block']}>
      <div className={classes.backdrop} onClick={onCloseNew}></div>
      <div className={`${classes['tab-title']} ${tabTitleColor}`}>
        <h2>{props.title}</h2>
        <h2>{filteredNum}</h2>
      </div>
      {taskList}
      {isShown && (
        <NewTask addTask={onAdd} onCloseNew={onCloseNew} />
      )}
      <div className={classes.actions}>
        <button
          className={classes['clean-done']}
          onClick={onDeleteDone}
        >
          清除已完成項目
        </button>
        <button className={classes['button-add']} onClick={onShowNew}>
          +
        </button>
      </div>
    </div>
  );
};

export default TaskBlock;
