import {useContext} from 'react';
import TaskContext from '../store/task-context';
import classes from "./Tab.module.css";

const Tab = (props)=>{
  const taskCtx = useContext(TaskContext)
  const undoTaskHandler = ()=>{
    const tabTasks = taskCtx.tasks.filter(task=> task.state===false);
    props.onTab({
      filteredTasks: tabTasks,
      title: '未完成'
    })
  }
  const doneTaskHandler = ()=>{
    const tabTasks = taskCtx.tasks.filter(task=> task.state===true);
    props.onTab({
      filteredTasks: tabTasks,
      title: '已完成'
    })
  }
  const allTaskHandler = ()=>{
    const tabTasks = taskCtx.tasks;
    props.onTab({
      filteredTasks: tabTasks,
      title: '全部'
    })
  }


  return <div className={classes.tab}>
    <ul>
      <li onClick={undoTaskHandler}>
        <span></span>
        <h3>未完成</h3>
      </li>
      <li onClick={doneTaskHandler}>
        <span></span>
        <h3>已完成</h3>
      </li>
      <li onClick={allTaskHandler}>
        <span></span>
        <h3>全部</h3>
      </li>
    </ul>
    </div>
};

export default Tab;
