import {MdDateRange} from 'react-icons/md';
import {BiCalendarCheck} from 'react-icons/bi';
import {FaRegListAlt} from 'react-icons/fa';
import { useEffect, useState } from 'react';
import classes from './Tab.module.css';

const Tab = (props) => {
  const [tabNum, setTabNum] = useState({todoNum: 0, doneNum: 0, allNum: 0})
  const [clickedTab, setClickedTab] = useState({
    todo: true,
    done: false,
    all: false,
  });
  const todoTaskHandler = () => {
    props.onTab({
      title: '未完成',
    });
    setClickedTab({
      todo: true,
      done: false,
      all: false,
    });
  };
  const doneTaskHandler = () => {
    props.onTab({
      title: '已完成',
    });
    setClickedTab({
      todo: false,
      done: true,
      all: false,
    });
  };
  const allTaskHandler = () => {
    props.onTab({
      title: '全部',
    });
    setClickedTab({
      todo: false,
      done: false,
      all: true,
    });
  };
  const todoClasses = `${classes['tab-item']} ${clickedTab.todo ? classes.active : ''}`
  const doneClasses = `${classes['tab-item']} ${clickedTab.done ? classes.active : ''}`
  const allClasses = `${classes['tab-item']} ${clickedTab.all ? classes.active : ''}`
  useEffect(()=>{
    const storedTasks = JSON.parse(localStorage.getItem('TASKS'));
    const todoNum = storedTasks.filter(item => !item.state).length
    const doneNum = storedTasks.filter(item => item.state).length
    const allNum = storedTasks.length
    setTabNum({todoNum, doneNum, allNum})

  },[])

  return (
    <div className={classes.tab}>
      <h3 className={classes['small-text']}>代辦事項</h3>
      <ul>
        <li className={todoClasses} onClick={todoTaskHandler}>
          <span className={classes.icon}><MdDateRange/></span>
          <p>未完成</p>
          <p className={classes.total}>{tabNum.todoNum}</p>
        </li>
        <li className={doneClasses} onClick={doneTaskHandler}>
          <span className={classes.icon}><BiCalendarCheck/></span>
          <p>已完成</p>
          <p className={classes.total}>{tabNum.doneNum}</p>
        </li>
        <li className={allClasses} onClick={allTaskHandler}>
          <span className={classes.icon}><FaRegListAlt/></span>
          <p>全部</p>
          <p className={classes.total}>{tabNum.allNum}</p>
        </li>
      </ul>
    </div>
  );
};

export default Tab;
