import {useState} from 'react';
import './App.css';
import TaskBlock from './component/Task/TaskBlock';
import Tab from './component/Tab';


function App() {
  const [filteredTitle, setFilteredTitle] = useState('');
  const [filteredTasks, setFilteredTasks] = useState([]);
  const captureTasksHandler = (obj)=>{
    setFilteredTitle(obj.title)
    setFilteredTasks(obj.filteredTasks);
  }

  return (
    <div className="App">
      <Tab onTab={captureTasksHandler} />
      <TaskBlock tabTasks={filteredTasks} title={filteredTitle} />
    </div>
  );
}

export default App;
