import React, {useReducer } from 'react';
import TaskContext from './task-context';

const initialState = {
  tasks: [],
  stateTasks: []
}
const taskReducer = (state,action)=>{
  if(action.type === 'ADD'){
    const enteredTaskIndex = state.tasks.findIndex(task=> task.id=== action.task.id)
    const enteredTask= state.tasks[enteredTaskIndex]
    let updateTasks;
    if(enteredTask){
      updateTasks = [...state.tasks]
      updateTasks[enteredTaskIndex] = action.task
    }else{
      updateTasks = state.tasks.concat(action.task);
    }
    return {tasks: updateTasks };

  }
  if(action.type=== 'TODO'){
    const updatedTasks = action.tasks.filter(task=> task.state===false)
    return {tasks: state.tasks , stateTasks: updatedTasks}
  }
  if(action.type=== 'DONE'){
    const updatedTasks = action.tasks.filter(task=> task.state===true)
    return {tasks: state.tasks , stateTasks: updatedTasks}
  }
  return initialState;
}
const TaskProvider = (props) => {

  const [state,dispatch]=useReducer(taskReducer , initialState)
  const addTaskHandler = (task)=>{
    dispatch({type:'ADD', task: task})
  }
  const todoListHandler = ()=>{
    dispatch({type: 'TODO'})
  }
  const doneListHandler = ()=>{
    dispatch({type: 'DONE'})
  }
  const taskContext ={
    tasks: state.tasks,
    stateTasks: state.stateTasks,
    addTask : addTaskHandler,
    todoList: todoListHandler,
    doneList: doneListHandler
  }



  return <TaskContext.Provider value={taskContext}>{props.children}</TaskContext.Provider>;
};

export default TaskProvider;
