import {useState} from 'react';
import './App.css';
import TaskBlock from './component/Task/TaskBlock';
import Tab from './component/Tab';


function App() {
  const [filteredTitle, setFilteredTitle] = useState('未完成');
  const captureTasksHandler = (title)=>{
    setFilteredTitle(title)
  }

  return (
    <div className="App">
      <Tab onTab={captureTasksHandler} />
      <TaskBlock title={filteredTitle} />
    </div>
  );
}

export default App;
