import { useRef } from 'react';
import classes from './NewTask.module.css';
const NewTask = (props) => {
  const taskRef = useRef();
  const radioRef = useRef();
  const inputValueHandler = (event) => {
    if(taskRef.current.value.trim()===''){
      return;
    }
    props.onTaskValue({
      id: Math.random(),
      value: taskRef.current.value,
      state: radioRef.current.checked,
    })
  };
  return (
    <div className={classes['new-task']} onBlur={inputValueHandler}>
      <input className={classes.checkbox} type="checkbox" ref={radioRef}/>
      <input className={classes.text} type="text" ref={taskRef}  />
    </div>
  );
};

export default NewTask;
