import classes from './TaskItem.module.css';

const TaskItem = (props) => {

  const stateChangeHandler = (event) => {
    props.onChangeState({
      id: props.id,
      value: props.value,
      state: !props.state,
    });
  };

  const styleClasses = `${classes.checkbox} ${props.state? classes.checked : ''}`
  return (
    <li key={props.id} className={classes['task']} onClick={stateChangeHandler}>
      <span className={styleClasses} />
      <div className={classes.text}>{props.value}</div>
    </li>
  );
};

export default TaskItem;
