// import {useContext} from 'react';
import NewTask from './NewTask';
import TaskItem from './TaskItem';
// import TaskContext from '../../store/task-context';

const TaskBlock = (props) => {
  // const taskCtx = useContext(TaskContext);
  let taskList = <p>沒有提醒事項</p>;

  if (props.title === '已完成') {
    const filteredTask = props.tasks.filter((task) => task.state === true);
    taskList = (
      <ul>
        {filteredTask.map((task) => (
          <TaskItem id={task.id} key={task.id} value={task.value} />
        ))}
      </ul>
    );
  }
  if (props.title === '未完成') {
    const filteredTask = props.tasks.filter((task) => task.state === false);
    taskList = (
      <ul>
        {filteredTask.map((task) => (
          <TaskItem id={task.id} key={task.id} value={task.value} />
        ))}
      </ul>
    );
  }

  if (props.title === '全部') {
    taskList = (
      <ul>
        {props.tasks.map((task) => (
          <TaskItem id={task.id} key={task.id} value={task.value} />
        ))}
      </ul>
    );
  }
  return (
    <div>
      <h2>{props.title}</h2>
      <NewTask />
      {taskList}
    </div>
  );
};

export default TaskBlock;
